mapper.select:
select
from tb_user_award_map uam,tb_shop s,tb_person_info buyer,tb_award a,tb_person_info o
uam.user_id = buyer.user_id and uam.shop_id = s.shop_id and uam.award_id = a.award_id and uam.operator_id = o.user_id
and uam.user_id=#{userAwardCondition.user.userId}
and uam.shop_id=#{userAwardCondition.shop.shopId}
and buyer.name like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and uam.used_status=#{userAwardCondition.usedStatus}
order by uam.create_time DESC limit #{rowIndex},#{pageSize}

select
from tb_user_award_map uam,tb_shop s,tb_person_info buyer,tb_award a,tb_person_info o
uam.user_id = buyer.user_id and uam.shop_id = s.shop_id and uam.award_id = a.award_id and uam.operator_id = o.user_id
and uam.user_id=#{userAwardCondition.user.userId}
and uam.shop_id=#{userAwardCondition.shop.shopId}
and buyer.name like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and uam.used_status=#{userAwardCondition.usedStatus}
order by uam.create_time DESC

select count(1) from tb_user_award_map uam,tb_shop s,tb_person_info buyer,tb_award a,tb_person_info o
uam.user_id = buyer.user_id and uam.shop_id = s.shop_id and uam.award_id = a.award_id and uam.operator_id = o.user_id
and uam.user_id=#{userAwardCondition.user.userId}
and uam.shop_id=#{userAwardCondition.shop.shopId}
and buyer.name= like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and uam.used_status=#{userAwardCondition.usedStatus}
