mapper.select:
select product_id, product_name, img_addr, product_desc, normal_price, promotion_price, create_time, enable_status, priority, point, last_edit_time, shop_id, product_category_id from tb_product
and shop_id = #{productCondition.shop.shopId}
and product_category_id = #{productCondition.productCategory.productCategoryId}
and product_name like '%${productCondition.productName}%'
and enable_status=#{productCondition.enableStatus}
order by priority DESC limit #{rowIndex},#{pageSize};

select product_id, product_name, img_addr, product_desc, normal_price, promotion_price, create_time, enable_status, point, priority, last_edit_time, shop_id, product_category_id from tb_product
and shop_id = #{productCondition.shop.shopId}
and product_category_id = #{productCondition.productCategory.productCategoryId}
and product_name like '%${productCondition.productName}%'
and enable_status=#{productCondition.enableStatus}

select count(1) from tb_product


        and shop_id = #{productCondition.shop.shopId}


        and product_category_id = #{productCondition.productCategory.productCategoryId}


        and product_name like '%${productCondition.productName}%'


        and enable_status=#{productCondition.enableStatus}

mapper.update:
update tb_product
set product_category_id = null
where product_category_id = #{productCategoryId}
